INSERT INTO `cms_html_module` VALUES ('156301172309105408', 'NEWS', 'news_list_3', 'news_list', 'static/cms/httplocalhost/img/jpg/44d6726a53ad4309.png_20180529140555_370x162.jpg', 'Laying kindergarten playgrounds with safe and comfortable artificial grass to keep children healthy and happy', '', 'Laying kindergarten playgrounds with safe and comfortable artificial grass to keep children healthy and happy', '05-29|2018', '', '05-29|2018', 'A kindergarten playground is an important place for children to learn\r\n                                and play. We hope that the kindergarten playground is beautiful and interesting, but we\r\n                                hope that it is safe and harmless. It also has beautiful interior and environmental\r\n                                protection. It should be the basic principle for kindergarten playground selection.\r\n                                SKYJADE Kindergarten Rainbow Grass Series is safe, environmentally friendly, colorful,\r\n                                and soft to the touch, making it ideal for kindergarten playgrounds.', '', 'A kindergarten playground is an important place for children to learn\r\n                                and play. We hope that the kindergarten playground is beautiful and interesting, but we\r\n                                hope that it is safe and harmless. It also has beautiful interior and environmental\r\n                                protection. It should be the basic principle for kindergarten playground selection.\r\n                                SKYJADE Kindergarten Rainbow Grass Series is safe, environmentally friendly, colorful,\r\n                                and soft to the touch, making it ideal for kindergarten playgrounds.', '', '', 'news-detail.html?title=Laying kindergarten playgrounds with safe and comfortable artificial grass to keep children healthy and happy', '', '', '', '', '', '', '', '', 3, 2);
INSERT INTO `cms_html_module` VALUES ('1563011723091254', 'NEWS', 'news_list_4', 'news_list', 'static/cms/httplocalhost/img/jpg/dd8ee4de3e242311.jpg_20180522151847_370x162.jpg', 'Sports artificial turf classification', '', 'Sports artificial turf classification', '05-22|2018', '', '05-22|2018', 'Different sports require different performances for the sports\r\n                                performance of the venue. So there are different models of sports artificial turf. There\r\n                                is a wear-resistant soccer artificial turf for football, a golf artificial green for the\r\n                                non-directional rolling of golf, and a tennis artificial turf for the bouncing\r\n                                performance of tennis.', '', 'Different sports require different performances for the sports\r\n                                performance of the venue. So there are different models of sports artificial turf. There\r\n                                is a wear-resistant soccer artificial turf for football, a golf artificial green for the\r\n                                non-directional rolling of golf, and a tennis artificial turf for the bouncing\r\n                                performance of tennis.', '', '', 'news-detail.html?title=Sports artificial turf classification', '', '', '', '', '', '', '', '', 4, 2);
INSERT INTO `cms_html_module` VALUES ('156301172309145389', 'NEWS', 'news_list_5', 'news_list', 'static/cms/httplocalhost/img/jpg/ebbcfe740a8b3008.jpg_20180515150140_370x162.jpg', 'Indoor lawn, restore a green spring for you indoors', '', 'Indoor lawn, restore a green spring for you indoors', '05-15|2018', '', '05-15|2018', 'Being close to nature seems to be a natural instinct for human\r\n                                beings. When we see green, we can feel happy. However, in a busy city, people rarely\r\n                                have leisure time fleeing into the embrace of nature to find a peaceful place. Then we\r\n                                had indoor lawns, green, soft, and naturally indoor lawns, and we sent nature to your\r\n                                feet.', '', 'Being close to nature seems to be a natural instinct for human\r\n                                beings. When we see green, we can feel happy. However, in a busy city, people rarely\r\n                                have leisure time fleeing into the embrace of nature to find a peaceful place. Then we\r\n                                had indoor lawns, green, soft, and naturally indoor lawns, and we sent nature to your\r\n                                feet.', '', '', 'news-detail.html?title=Indoor lawn, restore a green spring for you indoors', '', '', '', '', '', '', '', '', 5, 2);
INSERT INTO `cms_html_module` VALUES ('156301172309155388', 'NEWS', 'news_list_6', 'news_list', 'static/cms/httplocalhost/img/jpg/b98ee1e194587121.jpg_20180509085721_370x162.jpg', 'Proper maintenance and maintenance to increase the life of the artificial turf', '', 'Proper maintenance and maintenance to increase the life of the artificial turf', '05-09|2018', '', '05-09|2018', 'Artificial turf is similar in appearance and performance to real\r\n                                grass. It is wear-resistant, UV-resistant, anti-aging, and easy to maintain. However,\r\n                                due to its wide application, outdoor venues, long-term sun and rain, frequent use of\r\n                                people, high frequency, and complex and diverse environments, it will inevitably cause\r\n                                some effects on artificial turf. Therefore, there are still some issues that need\r\n                                attention in use.', '', 'Artificial turf is similar in appearance and performance to real\r\n                                grass. It is wear-resistant, UV-resistant, anti-aging, and easy to maintain. However,\r\n                                due to its wide application, outdoor venues, long-term sun and rain, frequent use of\r\n                                people, high frequency, and complex and diverse environments, it will inevitably cause\r\n                                some effects on artificial turf. Therefore, there are still some issues that need\r\n                                attention in use.', '', '', 'news-detail.html?title=Proper maintenance and maintenance to increase the life of the artificial turf', '', '', '', '', '', '', '', '', 6, 2);
INSERT INTO `cms_html_module` VALUES ('156301172309165437', 'NEWS', 'news_list_7', 'news_list', 'static/cms/httplocalhost/img/jpg/3633b829ff2bcea2.jpg_20180502173536_370x162.jpg', 'Whether the rubber particles filled in the artificial turf soccer field are harmful', '', 'Whether the rubber particles filled in the artificial turf soccer field are harmful', '05-02|2018', '', '05-02|2018', 'We all know that in order to achieve its sports performance,\r\n                                artificial turf on a football field increases the surface elasticity of the lawn and\r\n                                reduces sports injuries, and in order to maintain the erection of the grass, rubber\r\n                                granules must be filled therein. Previously there was an uproar in the nationwide &quot;toxic\r\n                                runway&quot; and its raw materials were rubber particles. Is rubber particle\r\n                                harmful?', '', 'We all know that in order to achieve its sports performance,\r\n                                artificial turf on a football field increases the surface elasticity of the lawn and\r\n                                reduces sports injuries, and in order to maintain the erection of the grass, rubber\r\n                                granules must be filled therein. Previously there was an uproar in the nationwide &quot;toxic\r\n                                runway&quot; and its raw materials were rubber particles. Is rubber particle\r\n                                harmful?', '', '', 'news-detail.html?title=Whether the rubber particles filled in the artificial turf soccer field are harmful', '', '', '', '', '', '', '', '', 7, 2);
INSERT INTO `cms_html_module` VALUES ('15630117230921536', 'NEWS', 'news_list_8', 'news_list', 'static/cms/httplocalhost/img/jpg/62ba97646743876f.jpg_20180424135717_370x162.jpg', 'Why do we choose indoor artificial grass carpet?', '', 'Why do we choose indoor artificial grass carpet?', '04-24|2018', '', '04-24|2018', 'Artificial turf carpets are emerging members of the carpeted family.\r\n                                Reasonable application of carpets in home decoration can not only play a role in\r\n                                beautifying, creating a soft and warm indoor environment but also can play the role of\r\n                                sound absorption and noise reduction. Artificial turf carpets can also be used in homes,\r\n                                office buildings, commercial shops, and other indoor places.', '', 'Artificial turf carpets are emerging members of the carpeted family.\r\n                                Reasonable application of carpets in home decoration can not only play a role in\r\n                                beautifying, creating a soft and warm indoor environment but also can play the role of\r\n                                sound absorption and noise reduction. Artificial turf carpets can also be used in homes,\r\n                                office buildings, commercial shops, and other indoor places.', '', '', 'news-detail.html?title=Why do we choose indoor artificial grass carpet?', '', '', '', '', '', '', '', '', 8, 2);
INSERT INTO `cms_html_module` VALUES ('156301172309235325', 'NEWS', 'news_list_9', 'news_list', 'static/cms/httplocalhost/img/jpg/69fe1f825b089e4b.jpg_20180417171140_370x162.jpg', 'Using artificial turf to create a close nature lawn weddings', '', 'Using artificial turf to create a close nature lawn weddings', '04-17|2018', '', '04-17|2018', 'Outdoor lawn weddings are becoming more and more new people&#39;s\r\n                                choice. Artificial turf is safe and environment-friendly, easy to clean, and it is very\r\n                                suitable for the laying and decoration of wedding venues from the aspect of appearance\r\n                                and use. Its fresh and natural, beautiful and elegant visual effects can create a very\r\n                                romantic and comfortable wedding atmosphere.', '', 'Outdoor lawn weddings are becoming more and more new people&#39;s\r\n                                choice. Artificial turf is safe and environment-friendly, easy to clean, and it is very\r\n                                suitable for the laying and decoration of wedding venues from the aspect of appearance\r\n                                and use. Its fresh and natural, beautiful and elegant visual effects can create a very\r\n                                romantic and comfortable wedding atmosphere.', '', '', 'news-detail.html?title=Using artificial turf to create a close nature lawn weddings', '', '', '', '', '', '', '', '', 9, 2);
