INSERT INTO `cms_html_module` VALUES ('156307635405913024', 'NEWS_DETAIL', 'news_list_23_1', 'news_list_23', '', '', '', '', '\r\n                <p style=\";margin-bottom:0;background:white;vertical-align:baseline\"><strong><span\r\n                        style=\"font-size: 12px;font-family:&#39;inherit&#39;,&#39;serif&#39;;color:#252525\">BBC Sport</span></strong>\r\n                </p>\r\n                <p style=\"background:white;vertical-align:baseline\"><strong><span\r\n                        style=\"font-family:&#39;Arial&#39;,&#39;sans-serif&#39;;color:#252525\">Manchester United &quot;should have been smoking cigars&quot; in their Premier League victory over Watford, manager Jose Mourinho said.</span></strong>\r\n                </p>\r\n                <p style=\"background: white;vertical-align: baseline;box-sizing: inherit;margin: 1.5rem;font-variant-numeric: inherit;font-stretch: inherit;line-height: inherit;outline-style: none\">\r\n                    <span style=\"color: rgb(37, 37, 37); font-family: arial, helvetica, sans-serif; font-size: 16px;\">United led 3-0 after 77 minutes, but Watford netted twice in quick succession for a tense finish, before Jesse Lingard scored a stunning fourth for the visitors, picking the ball up inside his own half before driving forward into the box to settle the match.</span>\r\n                </p>\r\n                <p style=\"background: white;vertical-align: baseline;box-sizing: inherit;margin: 1.5rem;font-variant-numeric: inherit;font-stretch: inherit;line-height: inherit;outline-style: none\">\r\n                    <span style=\"color: rgb(37, 37, 37); font-family: arial, helvetica, sans-serif; font-size: 16px;\">Ashley Young scored twice against his old club, drilling in a precise finish from the edge of the box, before curling in a stunning 25-yard free-kick six minutes later and Anthony Martial tucked in a cool finish from 18 yards out.</span>\r\n                </p>\r\n                <p style=\"backgro