INSERT INTO `cms_html_module` VALUES ('599208583111901207', 'FAQ', 'faq_faq_4', 'faq_faq', '', '4.Is SKYJADE Synthetic grass really maintenance-free?', '', '4.Is SKYJADE Synthetic grass really maintenance-free?', '4.Natural grass needs regular watering, mowing, fertilizing, weeding etc. Synthetic grass islow-maintenance and easily maintained. You will need to wash it down occasionally with a gardenhose, especially if you have pets. You may need to brush it with a broom or a gentle, plastic raketo keep it from looking trampled.', '', '4.Natural grass needs regular watering, mowing, fertilizing, weeding etc. Synthetic grass islow-maintenance and easily maintained. You will need to wash it down occasionally with a gardenhose, especially if you have pets. You may need to brush it with a broom or a gentle, plastic raketo keep it from looking trampled.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', 4, 2);
INSERT INTO `cms_html_module` VALUES ('599208583111901208', 'FAQ', 'faq_faq_5', 'faq_faq', '', '5.Is SKYJADE Synthetic grass safe for kids?', '', '5.Is SKYJADE Synthetic grass safe for kids?', '5.SKYJADE are an ideal surface for backyards frequented by children, Playgrounds, too. This syntheticturf can be played on every day. It stays clean. It’s hypo-allergenic, non-abrasive andnon-toxic.', '', '5.SKYJADE are an ideal surface for backyards frequented by children, Playgrounds, too. This syntheticturf can be played on every day. It stays clean. It’s hypo-allergenic, non-abrasive andnon-toxic.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', 5, 2);
INSERT INTO `cms_html_module` VALUES ('599208583111901209', 'FAQ', 'faq_faq_6', 'faq_faq', '', '6.Is SKYJADE Synthetic grass safe for pets?', '', '6.Is SKYJADE Synthetic grass safe for pets?', '6.Dogs and cats prefer the grass to other surfaces. It’s softer on their pads than dirt or concrete.It’s more sanitary than grass. Plus, it’s a snap to keep clean: Solid waste is easily removed fromthe fiber, urine passes through the turf and the backing, and all rinses away with water. ', '', '6.Dogs and cats prefer the grass to other surfaces. It’s softer on their pads than dirt or concrete.It’s more sanitary than grass. Plus, it’s a snap to keep clean: Solid waste is easily removed fromthe fiber, urine passes through the turf and the backing, and all rinses away with water. ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', 6, 2);
INSERT INTO `cms_html_module` VALUES ('599208583111901210', 'FAQ', 'faq_faq_7', 'faq_faq', '', '7.Is SKYJADE Synthetic grass safe for the environment?', '', '7.Is SKYJADE Synthetic grass safe for the environment?', '7.SKYJADE fiber, backing and infill complies with stringent laws and standards. Our product usesrecycled components. It’s 100% recyclable. It saves water. There are no grass clippings to cloglandfills, no pollution from a lawn mower’s engine, no fertilizers or added chemicals to contaminategroundwater.', '', '7.SKYJADE fiber, backing and infill complies with stringent laws and standards. Our product usesrecycled components. It’s 100% recyclable. It saves water. There are no grass clippings to cloglandfills, no pollution from a lawn mower’s engine, no fertilizers or added chemicals to contaminategroundwater.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', 7, 2);
INSERT INTO `cms_html_module` VALUES ('599208583111901211', 'FAQ', 'faq_faq_8', 'faq_faq', '', '8.Where does water go when it rains? Is an additional drain system necessary?', '', '8.Where does water go when it rains? Is an additional drain system necessary?', '8.SKYJADE backing features perforations every three inches rated at a drainage capability of morethan 10 inches of rain per hour. In most cases and climates, no separate drains are necessary; waterdrains naturally into the compacted aggregate sub-base.', '', '8.SKYJADE backing features perforations every three inches rated at a drainage capability of morethan 10 inches of rain per hour. In most cases and climates, no separate drains are necessary; waterdrains naturally into the compacted aggregate sub-base.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', 8, 2);
INSERT INTO `cms_html_module` VALUES ('599208583111901212', 'FAQ', 'faq_faq_9', 'faq_faq', '', '9.How long does synthetic grass last?', '', '9.How long does synthetic grass last?', '9.SKYJADE carries a manufacturer’s warranty guaranteeing the turf against fading and degradation for8- 10 years.', '', '9.SKYJADE carries a manufacturer’s warranty guaranteeing the turf against fading and degradation for8- 10 years.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', 9, 2);
